mapper.select:
select count(id)
from category
where is_delete = 0
  and name = #{name}
  and parent_id = #{parentId}

select id, parent_id, name, image, level, sort
from category

    is_delete = 0

        and name like concat('%', #{keyword}, '%')


        and parent_id = #{parentId}


        and name = #{name}


        and level = #{level}


        and is_show = #{isShow}


order by sort asc
